import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";
import "../../node_modules/react-bootstrap-table/css/react-bootstrap-table.css";

class Movies extends Component {
  state = {
    movies: getMovies(),
    like: false
  };

  buttonFormatter(cell, row) {
    return (
      <button
        onClick={() => {
          this.handleDelete(row);
        }}
        className="btn btn-danger btn-sm m-2"
      >
        Delete
      </button>
    );
  }

  showGenre(cell, row) {
    return cell.name;
  }

  heartFormatter(cell, row) {
    return (
      <i
        onClick={() => {
          console.log("heart cliced");
          this.handleLike();
        }}
        className={this.formatWithHeart()}
      />
    );
  }

  formatWithHeart() {
    const like = this.state.like;
    //this.testVarible = true;
    //console.log("values of like : " + this.testVarible);
    return like === true ? "fa fa-heart" : "fa fa-heart-o";
  }

  handleLike() {
    const like = this.state.like;
    console.log("values of like : " + like);
    //this.setState(like);
    return like === true ? false : like;
  }

  handleDelete(row) {
    console.log("Movie id : " + row._id);
    const movies = this.state.movies.filter(c => c._id !== row._id);
    this.setState({ movies });
    this.countMovies();
  }

  countMovies() {
    const numMovies = this.state.movies.length;
    if (numMovies > 0) {
      return "Showing " + numMovies + " movies in the database";
    } else {
      return "No movies in the database";
    }
  }

  render() {
    return (
      <main className="container">
        <h4>{this.countMovies()}</h4>
        <div className="Movies">
          <BootstrapTable data={this.state.movies}>
            <TableHeaderColumn isKey dataField="_id" hidden>
              ID
            </TableHeaderColumn>
            <TableHeaderColumn dataField="title" width="20%">
              Title
            </TableHeaderColumn>
            <TableHeaderColumn
              dataField="genre"
              dataFormat={this.showGenre}
              width="20%"
            >
              Genre
            </TableHeaderColumn>
            <TableHeaderColumn dataField="numberInStock" width="10%">
              Stock
            </TableHeaderColumn>
            <TableHeaderColumn dataField="dailyRentalRate" width="10%">
              Rate
            </TableHeaderColumn>
            <TableHeaderColumn
              dataField="heart"
              dataFormat={this.heartFormatter.bind(this)}
              width="10%"
              dataAlign="center"
            />

            <TableHeaderColumn
              dataField="button"
              width="10%"
              dataFormat={this.buttonFormatter.bind(this)}
            >
              Buttons
            </TableHeaderColumn>
          </BootstrapTable>
        </div>
      </main>
    );
  }
}

export default Movies;
