import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";
import "../../node_modules/react-bootstrap-table/css/react-bootstrap-table.css";
import Like from "./common/like";

class Movies extends Component {
  state = {
    movies: getMovies(),
    currentPage: 1,
    moviesPerPage: 3
  };

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }

  handleLike(movie) {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movie.liked;
    this.setState({ movies });
  }

  handleDelete(movie) {
    const movies = this.state.movies.filter(c => c._id !== movie._id);
    this.setState({ movies });
    this.countMovies();
  }

  countMovies() {
    const numMovies = this.state.movies.length;
    if (numMovies > 0) {
      return "Showing " + numMovies + " movies in the database";
    } else {
      return "No movies in the database";
    }
  }

  render() {
    const { movies, currentPage, moviesPerPage } = this.state;

    // Logic for displaying movies
    const indexOfLastTodo = currentPage * moviesPerPage;
    const indexOfFirstTodo = indexOfLastTodo - moviesPerPage;
    const currentMovies = movies.slice(indexOfFirstTodo, indexOfLastTodo);

    const rendermovies = currentMovies.map((todo, index) => {
      return <li key={index}>{todo}</li>;
    });

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(movies.length / moviesPerPage); i++) {
      pageNumbers.push(i);
    }

    return (
      <main className="container">
        <h4>{this.countMovies()}</h4>
        <div className="Movies">
          <table className="table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Genre</th>
                <th>Stock</th>
                <th>Rate</th>
                <th />
                <th />
              </tr>
            </thead>
            <tbody>
              {this.state.movies.map(movie => (
                <tr>
                  <td>{movie.title}</td>
                  <td>{movie.genre.name}</td>
                  <td>{movie.numberInStock}</td>
                  <td>{movie.dailyRentalRate}</td>
                  <td>
                    <Like
                      liked={movie.liked}
                      onClick={() => this.handleLike(movie)}
                    />
                  </td>
                  <td>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => this.handleDelete(movie)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    );
  }
}

export default Movies;
