import React, { Component } from "react";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";
//import "../css/Table.css";
import "../../node_modules/react-bootstrap-table/css/react-bootstrap-table.css";

function buttonFormatter(cell, row) {
  return (
    <button
      onClick={() => this.props.onDelete(this.props.movie.id)}
      className="btn btn-danger btn-sm m-2"
    >
      Delete
    </button>
  );
}

class Table1 extends Component {
  render() {
    return (
      <div>
        <BootstrapTable data={this.props.data}>
          <TableHeaderColumn isKey dataField="_id" hidden>
            ID
          </TableHeaderColumn>
          <TableHeaderColumn dataField="title" width="20%">
            Title
          </TableHeaderColumn>

          <TableHeaderColumn dataField="numberInStock" width="10%">
            Stock
          </TableHeaderColumn>
          <TableHeaderColumn dataField="dailyRentalRate" width="10%">
            Rate
          </TableHeaderColumn>
          <TableHeaderColumn dataField="button" dataFormat={buttonFormatter}>
            Buttons
          </TableHeaderColumn>
        </BootstrapTable>
      </div>
    );
  }
}

export default Table1;
