import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";
import "../../node_modules/react-bootstrap-table/css/react-bootstrap-table.css";

class Movies extends Component {
  state = {
    movies: getMovies()
  };

  buttonFormatter(row) {
    return (
      <button
        onClick={() => {
          this.handleDelete(row);
        }}
        className="btn btn-danger btn-sm m-2"
      >
        Delete
      </button>
    );
  }

  handleDelete(row) {
    console.log("Movie id : " + row);
    //const movies = this.state.movies.filter(c => c._id !== movieId);
    //this.setState({ movies });
  }

  render() {
    return (
      <main className="container">
        <div className="Movies">
          <BootstrapTable data={this.state.movies}>
            <TableHeaderColumn isKey dataField="_id" hidden>
              ID
            </TableHeaderColumn>
            <TableHeaderColumn dataField="title" width="20%">
              Title
            </TableHeaderColumn>

            <TableHeaderColumn dataField="numberInStock" width="10%">
              Stock
            </TableHeaderColumn>
            <TableHeaderColumn dataField="dailyRentalRate" width="10%">
              Rate
            </TableHeaderColumn>
            <TableHeaderColumn
              dataField="button"
              width="10%"
              dataFormat={this.buttonFormatter.bind(this)}
            >
              Buttons
            </TableHeaderColumn>
          </BootstrapTable>
        </div>
      </main>
    );
  }
}

export default Movies;
