import React, { Component } from "react";

class Like extends Component {
  render() {
    return (
      <i
        onClick={() => this.props.onClick()}
        className={this.formatWithHeart()}
      />
    );
  }

  formatWithHeart() {
    const like = this.state.like;
    //console.log("Movie id : " + row._id);
    //this.testVarible = true;
    //console.log("values of like : " + this.testVarible);
    return like === true ? "fa fa-heart" : "fa fa-heart-o";
  }
}

export default Like;
