import React from "react";

const Listgroup = props => {
  const {
    genres,
    textProperty,
    valueProperty,
    currentGenre,
    onFilterChange
  } = props;

  return (
    <ul className="list-group">
      <li
        className={
          currentGenre === "All genres"
            ? "list-group-item active"
            : "list-group-item"
        }
        style={{ cursor: "pointer" }}
        onClick={() => onFilterChange("All genres")}
      >
        All genres
      </li>
      {genres.map(genre => (
        <li
          key={genre[valueProperty]}
          className={
            genre[textProperty] === currentGenre
              ? "list-group-item active"
              : "list-group-item"
          }
          style={{ cursor: "pointer" }}
          onClick={() => onFilterChange(genre.name)}
        >
          {genre[textProperty]}
        </li>
      ))}
    </ul>
  );
};

export default Listgroup;
