import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";
import "../../node_modules/react-bootstrap-table/css/react-bootstrap-table.css";
import Like from "./common/like";
import Pagination from "./common/pagination";
import { paginate } from "./utils/paginate";
import Listgroup from "./common/listgroup";

class Movies extends Component {
  state = {
    movies: getMovies(),
    genres: getGenres(),
    currentPage: 1,
    currentGenre: "All genres",
    pageSize: 4
  };

  handlePageChange = page => {
    this.setState({ currentPage: page });
  };

  handleFilterChange = genre => {
    console.log("selected genre : " + genre);
    this.setState({ currentGenre: genre });
  };

  handleLike(movie) {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movie.liked;
    this.setState({ movies });
  }

  handleDelete(movie) {
    const movies = this.state.movies.filter(c => c._id !== movie._id);
    this.setState({ movies });
  }

  countMovies() {
    const numMovies = this.state.movies.length;
    if (numMovies > 0) {
      return "Showing " + numMovies + " movies in the database";
    } else {
      return "No movies in the database";
    }
  }

  render() {
    const { currentPage, pageSize } = this.state;
    const count = this.state.movies.length;

    const movies = paginate(this.state.movies, currentPage, pageSize);

    return (
      <main className="container">
        <h4>{this.countMovies()}</h4>
        <div className="row">
          <div className="column" style={{ width: 150 }}>
            <Listgroup
              genres={this.state.genres}
              onFilterChange={this.handleFilterChange}
            />
          </div>
          <div className="column" style={{ width: 750 }}>
            <table className="table">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Genre</th>
                  <th>Stock</th>
                  <th>Rate</th>
                  <th />
                  <th />
                </tr>
              </thead>
              <tbody>
                {movies.map(movie => (
                  <tr key={movie._id}>
                    <td>{movie.title}</td>
                    <td>{movie.genre.name}</td>
                    <td>{movie.numberInStock}</td>
                    <td>{movie.dailyRentalRate}</td>
                    <td>
                      <Like
                        liked={movie.liked}
                        onClick={() => this.handleLike(movie)}
                      />
                    </td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => this.handleDelete(movie)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            <Pagination
              itemsCount={count}
              pageSize={pageSize}
              currentPage={currentPage}
              onPageChange={this.handlePageChange}
            />
          </div>
        </div>
      </main>
    );
  }
}

export default Movies;
